import styled from "styled-components";

export const Container =styled.div`
    display:flex;
    justify-content:space-evenly;
    align-items:center;
    flex-direction: column;
    width:50%;
    height: 80%;
    text-align: center;
    margin:auto;
    


    button{
        justify-self:flex-end;
        max-width:40%;

    }


    img{
        max-width:40%;
        
    }
`
