import styled from "styled-components";


export const CardContainer = styled.div`

background-color: var(--soft-white);
padding: 10px;
margin: 5px;
text-align: center ;
color: var(--dark);


img{
    max-width:200px;
}

h2 {
    color: var(--deep-blue);
}



`

export const ContainerBotao =styled.div`
display:flex;
align-items:center;
justify-content:space-between;
margin: 10px 15px 10px 15px;
`
